
export function menu(){const $openMenu=document.querySelector(".openMenu");const $closeMenu=document.querySelector(".closeMenu");const $menu=document.querySelector(".mainMenu");const $menuButtons=document.querySelectorAll(".mainMenu li a");$openMenu.addEventListener("click",()=>{$menu.classList.add("showMenu");document.querySelector("html").classList.add("isMenuOpen");});$closeMenu.addEventListener("click",()=>{$menu.classList.remove("showMenu");document.querySelector("html").classList.remove("isMenuOpen");});$menuButtons.forEach((el)=>{el.addEventListener("click",()=>{$menu.classList.remove("showMenu");document.querySelector("html").classList.remove("isMenuOpen");return;});});}